import { create } from "@/api/company";

export default {
  data() {
    return {
      loading: false,
      form: {
        title: null
      },
      rules: {
        title: [
          {
            required: true
          }
        ]
      }
    };
  },
  methods: {
    onSubmit(formName) {
      this.$refs[formName].validate(valid => {
        if (valid) {
          this.loading = true;
          create(this.form)
            .then(response => {
              this.$message({
                message: "Cadastrado realizado com sucesso",
                type: "success",
                duration: 5 * 1000
              });
              this.$refs[formName].resetFields();
            })
            .finally(responde => {
              this.loading = false;
            });
        }
      });
    }
  }
};
