import { getList } from '@/api/company'

export default {
  filters: {
    statusFilter(status) {
      const statusMap = {
        published: 'success',
        draft: 'gray',
        deleted: 'danger'
      }
      return statusMap[status]
    }
  },
  data() {
    return {
      list: null,
      listLoading: true
    }
  },
  created() {
    this.fetchData()
  },
  methods: {
    fetchData(page = 1) {
      this.listLoading = true
      var params = {
        page: page
      }
      getList(params).then(response => {
        this.list = response.data
        this.listLoading = false
      })
    }
  }
}
